import { Button, Image, Space, Row } from "antd";
import React, { useState, useLayoutEffect, useEffect } from "react";
import { convertCompilerOptionsFromJson } from "typescript";
import EditDetail from "../Edit Detail/Edit Detail";


const UploadPhoto = ({idPhoto}) => {
    const [file, setFile] = useState("error");
    const [fileLoad, setFileLoad] = useState("error");
    const [photos, setPhotos] = useState();
    const [index, setIndex] = useState(-1);
    
    function handleChange(e) {
        console.log(e.target.files);
        setFileLoad(URL.createObjectURL(e.target.files[0]));
    }

    function nextPhoto() {
      console.log(file);
      console.log(index);
      let nowIndex = index + 1;
      if (nowIndex < photos.length) {
      let photo = photos[nowIndex];
      console.log(photo)
      import(`./../../photos/${photo}`).then(res => {
        setIndex(nowIndex);
        setFile(res.default)
      })
     }
    }

    function prevPhoto() {
      console.log(file);
      console.log(index);
      let nowIndex = index - 1;
      if (nowIndex > -1) {
      let photo = photos[nowIndex];
      console.log(photo)
      import(`./../../photos/${photo}`).then(res => {
        setIndex(nowIndex);
        setFile(res.default)
      })
     }
    }

    const handleDelete = () => {
      const token = localStorage.getItem('accessToken').replaceAll("\"", "");
      if (photos.length != 0) {
      let photo = photos[index];
      fetch(process.env.REACT_APP_DETAIL + `/${photo}/photo`, {
        method: 'DELETE',
        headers: {
            'Authorization' :'Bearer ' + token,
        }
    }).then(res => 
        {
          if (res.status == 403) {
            localStorage.removeItem('accessToken');
            throw new Error ("Время сессии истекло")
          } else {
            let arrPhoto = photos;
            arrPhoto.splice(index, 1);
            setPhotos(arrPhoto);
            if (photos.length != 0) {
            let photo = photos[0];
            import(`./../../photos/${photo}`).then(res => {
            setIndex(0);
            setFile(res.default)
            })
            } else {
              setFile("error");
            }
            return;
          }
        }).catch((res) => {
          alert(res);
          window.location.reload();
        });
      }
    };

    useEffect(() => {
        console.log(idPhoto);
        getPhotos(idPhoto);
     }, []);

     function getPhotos(idPhoto) {
        return fetch(process.env.REACT_APP_DETAIL + `/${idPhoto}/photos`, {
          method: 'GET',
          headers: {
              'Authorization' :'Bearer ' + localStorage.getItem('accessToken').replaceAll("\"", ""),
              'Content-Type': 'application/json;charset=utf-8'
          },
      }).then((res) =>  {
        if (res.status == 403) {
          localStorage.removeItem('accessToken');
          throw new Error ("Время сессии истекло")
        } else {
          return res.json();
        }
      })
      .then((results) => {
          console.log(results);
          setPhotos(results);
          //setFile(results[0])
          if (results.length != 0) {
          let photo = results[0];
          import(process.env.REACT_APP_PHOTO_STORAGE + `/${photo}`).then(res => {
            console.log(res);
            setIndex(0);
            setFile(res.default)
          })
        }
          return results;
      }).catch((res) => {
        alert(res);
        window.location.reload();
      });
    }

    const onFinish = () => {
        const token = localStorage.getItem('accessToken').replaceAll("\"", "");
        let tableElem = document.getElementById('inputPhoto');

        console.log(tableElem.files[0]);
        let data = new FormData();
        data.append('avatar', tableElem.files[0]);

        fetch(process.env.REACT_APP_DETAIL + `/${idPhoto}/add-avatar`, {
            method: 'POST',
            headers: {
                'Authorization' :'Bearer ' + token
            },
            body: data
        })
        .then((res) =>  {
            if (res.status == 403) {
              localStorage.removeItem('accessToken');
              throw new Error ("Время сессии истекло")
            } else {
              return res.json();
            }}).then(res=> {
              console.log(res.fileName);
              let arrPhoto = photos;
              arrPhoto.push(res.fileName);
              setPhotos(arrPhoto);
              if (photos.length == 1) {
              let photo = photos[0];
              setTimeout(() => import(process.env.PHOTO_STORAGE + `/${photo}`).then(res => {
                setIndex(0);
                setFile(res.default)
              }), 1500);
            }
              return res;
            }
            )
          .catch((res) => {
            alert(res);
            window.location.reload();
          });
      };
    
  
    return (
      <Row gutter={16}>
        <Space direction="vertical">
            <h2>Добавить фото:</h2>
            <Image height = {200} src= {fileLoad} fallback="data:image/png;base64,"/>
            <input type="file" onChange={handleChange} id = "inputPhoto"/>
            <Button type ='primary' onClick={onFinish} id ={idPhoto}>Сохранить фото</Button>
        </Space>
        <Space direction="vertical">
             <h2>Набор фото:</h2>
            <Image  height = {200} src={file} fallback="data:image/png;base64,"/>
            <Space direction="horizontal">
            <Button type="primary" shape="circle" onClick={prevPhoto}>Предыдущее</Button>
            <Button type="primary" shape="round" danger onClick={handleDelete}>Удалить</Button>
            <Button type="primary" shape="circle" onClick={nextPhoto}>Следующее</Button>
            </Space>
        </Space>
      </Row>
    );
};

export default UploadPhoto;